import Board from './Board'
import ListGroup from 'react-bootstrap/ListGroup';
import Card from 'react-bootstrap/Card';

const Boards = ({ boards, onDelete, onToggle }) => {
    return (
        <>
            <Card border="info" style={{ width: '30rem' }}>
                <Card.Header>
                    <p>Boards</p>
                </Card.Header>
                <Card.Body>
                    {Boards}
                </Card.Body>

            </Card>
            <ListGroup as="ul">
                {boards.map((board, index) => (
                        <Board key={index} board={board} onDelete={onDelete} onToggle={onToggle} />


                ))}
            </ListGroup>
        </>
    )
}

export default Boards
